class Solution:
    def numMatchingSubseq(self, s: str, words: list[str]) -> int:
        # Create mapping of number of available chars for each letter in s
        available = {}
        for c in s:
            if c not in available:
                available[c] = 1
            else:
                available[c] += 1
        print(str(available.sort))
        # For each word, copy available, subtracting one from each slot on occurence of a letter
        # Skip the word if either 1) not enough letters, 2) letter not found in available
        count = 0
        for word in words:
            curr_available = available.copy()
            use = True
            for c in word:
                if (c not in curr_available) or (curr_available[c] - 1 < 0):
                    use = False
                    break
                curr_available[c] -= 1
            if not use:
                use = True
            else:
                count += 1
        # Return amount of words
        return count



